  "log": {
    "loglevel": "debug"
  },
  "inbounds": [
    {
      "port": 62121,
      "protocol": "vmess",
      "settings": {
        "clients": [
          {
            "id": "xxxxxxxx-xxxx-xxxx-xxxx-xxxxxxxx",
            "level": 0,
            "alterId": 64
          }
        ]
      },
      "streamSettings": {
        "network": "tcp",
        "security": "tls",
        "tlsSettings": {
          "allowInsecure": false,
          "certificates": [
              {
                "certificateFile": "/etc/nginx/certs/example.com/fullchain.cer",
                "keyFile": "/etc/nginx/certs/example.com/example.com.key"
              }
            ]
        },
        "quicSettings": {
          "security": "chacha20-poly1305",
          "key": "zzzzzzzz-zzzz-zzzz-zzzz-zzzzzzzz",
          "header": {
            "type": "none"
          }
        }
      }   
    }
  ],
  "outbounds": [
    {
      "protocol": "freedom",
      "settings": {}
    }
  ]
}

